import React,{useState,useEffect} from 'react'
import './estilos.css'
import Socket from '../socket'
import axios from 'axios'
import Moment from 'moment'
import {extendMoment} from 'moment-range'

export default function Table() {
    const moment = extendMoment(Moment)
    const [checadas,setChecadas] = useState({})
    const checadasFinal=[]
    const dias=[]
    useEffect(()=>{
        const fechData = async()=>{
            const res = await axios.get('http://localhost:4000/api/checadas/')
            setChecadas(res.data)
        }
        fechData()
        Socket.on('checada',(data)=>{
            console.log(data)
        })
    },[])

    const renderChecada =()=>{
        const inicio = moment([moment().startOf('month').add(1,'days').format('YYYY-MM-DD')])
        const final = moment([moment().startOf('month').add(15,'days').format('YYYY-MM-DD')])
        console.log(inicio.diff(final,'days'))
        if(checadas.length>0){
            checadas.map(checada=>
                {
                    const filtro = checadasFinal.filter(filtro => filtro.empleado === checada.empleado)
                    if (filtro.length===0) {
                        const dias = checadas.filter(empleado=>empleado.empleado===checada.empleado)
                        checadasFinal.push({
                            empleado:checada.empleado,
                            dias:dias
                        })
                    }
                }
            )
        }
        console.log(dias)
    }
    return (
        <table className="table table-bordered tableAsisten">
            {renderChecada()}
            <thead className="thead">
                <tr>
                    <th scope="col">Nombre</th>
                    <th scope="col" className="dia">Lunes 31 agosto</th>
                    <th scope="col" className="dia">Martes 01 septiembre</th>
                    <th scope="col" className="dia">Miercoles 02 septiembre</th>
                    <th scope="col" className="dia">Jueves 03 septiembre</th>
                    <th scope="col" className="dia">Viernes 04 septiembre</th>
                    <th scope="col" className="dia">Sabado 05 2septiembre</th>
                    <th scope="col" className="dia">Domingo 06 septiembre</th>
                </tr>
            </thead>
            <tbody>
            {checadasFinal.map(empleado=>
            <tr key={empleado.empleado}>
                <td>{empleado.empleado}</td>
                <td >
                    <span className="badge bg-checada btn">
                        <i className="far fa-check-circle btn-block h4"></i> 
                        8:50-5:10
                    </span>
                </td>
                <td >
                    <span className="badge bg-falta btn">
                        <i className="far fa-times-circle btn-block h4"></i> 
                        8:50-5:10
                    </span>
                </td>
                <td>8:50-5:10</td>
                <td>8:50-5:10</td>
                <td>8:50-5:10</td>
                <td>8:50-5:10</td>
                <td>8:50-5:10</td>
            </tr>
            )}

            </tbody>
        </table>
    )
}
